import React from "react";
import "../Styles/NavBar.css";
import {Navbar, Nav, Container, Col, Row, Button } from "react-bootstrap";
import logoIMG from "../Resources/RC_LOGO.png";
import { HashLink } from "react-router-hash-link";
import { BrowserRouter as Router } from "react-router-dom";
import { useState, useEffect } from "react";

export const NavBar = () => {
  const [activeLink, setActiveLink] = useState("home");
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const onUpdateActiveLink = (value) => {
    setActiveLink(value);
  };

  return (
    <Router> 
      <Navbar expand="md" className={scrolled ? "scrolled" : ""}>
        <Container>
          <Navbar.Brand href="#">Renke Cui</Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarNavAltMarkup" />
          <Navbar.Collapse id="navbarNavAltMarkup">
          <Row className="align-items-center text-center Home-Header">
            <Col md={1}>
              {/* <div className="logo"> */}
              <Navbar.Brand href="/">                  <img src={logoIMG} alt="Renke Cui Logo" height="100" />{" "}
                </Navbar.Brand>
                {/* <Link to="/Home" aria-label="Home">
                <img src={logoIMG} alt="Renke Cui Logo" height="100" />
              </Link> */}
              {/* </div> */}
            </Col>
            <Col md={7}> </Col>
            <Col md={1}>
              <div className="projects-link nav-link">
                {/* <Link
                to="/Projects"
                className={`nav-link ${
                  pathname.includes("Projects") ? "active" : ""
                }`}
                aria-current={
                  pathname.includes("Projects") ? "page" : undefined
                }
              >
                <Button variant="secondary">Projects</Button>{" "}
              </Link> */}
              </div>
            </Col>
            <Col md={2}>
              <div className="about-link nav-link">
                {/* <Link
                to="/About"
                className={`nav-link ${
                  pathname.includes("About") ? "active" : ""
                }`}
                aria-current={pathname.includes("About") ? "page" : undefined}
              >
                <Button variant="secondary">About Me</Button>{" "}
              </Link> */}
              </div>
            </Col>
            <Col md={1}> </Col>
          </Row>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </Router>
  );
};

export default NavBar;
